import { MachineConfig, send, Action, assign } from "xstate";

const proxyurl = "https://cors-anywhere.herokuapp.com/";
const rasaurl = 'https://assignment2nlu.herokuapp.com/model/parse'
const nluRequest = (text: string) =>
    fetch(new Request(proxyurl + rasaurl, {
        method: 'POST',
        headers: { 'Origin': 'http://maraev.me' }, // only required with proxy
        body: `{"text": "${text}"}`
    }))
        .then(data => data.json());



function say(text: string): Action<SDSContext, SDSEvent> {
    return send((_context: SDSContext) => ({ type: "SPEAK", value: text }))
}

function listen(): Action<SDSContext, SDSEvent> {
    return send('LISTEN')
}

const grammar: { [index: string]: { person?: string, day?: string, time?: string } } = {
    "John": { person: "John Appleseed" },
    "Ridley": { person: "Ridley Scott" },
    "Steven": { person: "Steven Spielberg" },
    "Wes": { person: "Wes Andreson" },
    "Stanley": { person: "Stanley Kubrick" },
    "David": { person: "David Lynch" },
    "Ingmar": { person: "Ingmar Bergman" },
    "Alfred": { person: "Alfred Hitchcock "},

    "on Friday": { day: "Friday" },
    "on Monday": { day: "Monday" },
    "Monday": { day: "Monday"},
    "Friday": { day: "Friday" },
    "Tuesday": { day: "Tuesday" },
    "Thursday": { day: "Thursday" },
    "on Tuesday": { day: "Tuesday" },
    "on Wednesday": { day: "Wednesday" },
    "Wednesday": { day: "Wednesday" },
    "on Thursday": { day: "Thursday" },
    "on Saturday": { day: "Saturday" },
    "Saturday": { day: "Saturday" },
    "on Sunday": { day: "Sunday" },
    "Sunday": { day: "Sunday" },

    "6": { time: "6:00" },
    "at 6": { time: "6:00" },
    "7": { time: "7:00" },
    "at 7": { time: "7:00" },
    "8": { time: "8:00" },
    "at 8": { time: "8:00" },
    "9": { time: "9:00" },
    "at 9": { time: "9:00" },
    "10": { time: "10:00" },
    "at 10": { time: "10:00" },
    "11": { time: "11:00" },
    "at 11": { time: "11:00" },
    "12": { time: "12:00" },
    "at 12": { time: "12:00" },
    "13": { time: "13:00" },
    "at 13": { time: "13:00" },
    "14": { time: "14:00" },
    "15": { time: "15:00" },
    "16": { time: "16:00" },
    "17": { time: "17:00" },
    "18": { time: "18:00" },
    "19": { time: "19:00" },

}

const boolGrammar = {
      "yes": true,
      "of course": true,
      "yep": true,
      "nope": false,
      "no": false,
      "no way": false,
      "never": false,
}


export const dmMachine: MachineConfig<SDSContext, any, SDSEvent> = ({
    initial: 'init',
    states: {
        init: {
            on: {
                CLICK: 'who'
            }
        },

        Appointment: {
            initial: "prompt",
            on: { ENDSPEECH: "who" },
            states: {
                prompt: { entry: say("Let's create an appointment") }
            }
        },
        who: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "person" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { person: grammar[context.recResult].person } }),
                    target: "day"

                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: say("Who are you meeting with?"),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry I don't know them"),
                    on: { ENDSPEECH: "prompt" }
                }
            }
        },
        day: {
            initial: "prompt",
            on:{
                RECOGNISED: [{
                    cond: (context) => "day" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { day: grammar[context.recResult].day } }),
                    target: "duration"
                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `OK. ${context.person}. On which day is your meeting?`
                    })),
		    on: { ENDSPEECH: "ask" }
                },
		ask: {
		     entry: listen()
            },
	    nomatch: {
	    	entry: say("Sorry, I don't understand"),
		on: { ENDSPEECH: "prompt" }
	    }
	  }
        },
	duration: {
            initial: "prompt",
            on: {
                RECOGNISED: [{cond: (context) => (boolGrammar[context.recResult] === false),
                    target: "time"
                },
		{cond: (context) => (boolGrammar[context.recResult] === true),
		target: "confirm_meeting_whole_day"
		},
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `OK. ${context.person} on ${context.day}. Will it take the whole day?`
                    })),
		    on: { ENDSPEECH: "ask" }
                },
		ask: {
		     entry: listen()
            },
	    nomatch: {
	    	entry: say("Sorry, I don't understand"),
		    on: { ENDSPEECH: "prompt" }
	            }
        }
	},
	time: {
            initial: "prompt",
            on: { RECOGNISED: [{
                    cond: (context) => "time" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { time: grammar[context.recResult].time } }),
                    target: "confirm_time"
                },
                { target: ".nomatch" }]
		},
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `OK. ${context.person} on ${context.day}. What time is your meeting?`
                    })),
		    on: { ENDSPEECH: "ask" }
                },
		ask: {
		     entry: listen()
            },
	    nomatch: {
	    	entry: say("Sorry, I don't understand"),
		on: { ENDSPEECH: "prompt" }
	            }
          }
    },
	confirm_meeting_whole_day: {
            initial: "prompt",
            on: {
                RECOGNISED: [{cond: (context) => (boolGrammar[context.recResult] === false),
                    target: "init"
                },
		{cond: (context) => (boolGrammar[context.recResult] === true),
		target: "confirmed"
		},
                { target: ".nomatch" }]
            },

            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Do you want to create an appointment with ${context.person} on ${context.day} for the whole day?`
                    })),
		    on: { ENDSPEECH: "ask" }
                },
		ask: {
		     entry: listen()
            },
	    nomatch: {
	    	entry: say("Sorry, I don't understand"),
		on: { ENDSPEECH: "prompt" }
	        }
         }
	},
 	confirm_time: {
            initial: "prompt",
            on:  {
                RECOGNISED: [{cond: (context) => (boolGrammar[context.recResult] === false),
                    target: "who"
                },
		{cond: (context) => (boolGrammar[context.recResult] === true),
		target: "confirmed"
		},
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                   entry: send((context) => ({
                        type: "SPEAK",
                        value: `Do you want to create an appointment with ${context.person} on ${context.day} at ${context.time}?`
                    })),
		    on: { ENDSPEECH: "ask" }
                },
		ask: {
		     entry: listen()
            },
	    nomatch: {
	    	entry: say("Sorry, I don't understand"),
		on: { ENDSPEECH: "prompt" }
	        }
         },
    },
	confirmed: {
            initial: "prompt",
            on: { ENDSPEECH: "init" },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Your appointment has been created!`
                    }))
                },
		}
	}
}})
