import { MachineConfig, send, Action, assign } from "xstate";


function say(text: string): Action<SDSContext, SDSEvent> {
    return send((_context: SDSContext) => ({ type: "SPEAK", value: text }))
}

function listen(): Action<SDSContext, SDSEvent> {
    return send('LISTEN')
}

const grammar: { [index: string]: { person?: string, day?: string, time?: string } } = {
    "John": { person: "John Appleseed" },
    "Joan": { person: "Joan Orange" },
    "on Friday": { day: "Friday" },
    "on Monday": { day: "Monday" },
    "at ten": { time: "10" },
    "10": { time: "10" },
    "11": { time: "11:00" },
}

const boolGrammar = {
      "yes": true,
      "of course": true,
      "no": false,
      "no way": false,
      "never": false,
}

let a = boolGrammar["yes"]
let b = boolGrammar["no"]

const menuGrammar: { [index: string]: { activity?: string} } = {
    "appointment": { activity: "Appointment" },
    "to do": { activity: "TODOitem" },
    "timer": { activity: "Timer" },
}


export const dmMachine: MachineConfig<SDSContext, any, SDSEvent> = ({
    initial: 'init',
    states: {
        init: {
            on: {
                CLICK: 'menu'
            }
        },

	menu: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "activity" in (menuGrammar[context.recResult] || {}) && context.recResult === "timer",
                    actions: assign((context) => { return { activity: menuGrammar[context.recResult].activity } }),
                    target: "Timer"

                },
		{cond: (context) => "activity" in (menuGrammar[context.recResult] || {}) && context.recResult === "to do",
                    actions: assign((context) => { return { activity: menuGrammar[context.recResult].activity } }),
                    target: "TODOitem"
		    },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: say("What would you like to do?"),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry I don't understand."),
                    on: { ENDSPEECH: "prompt" }
                }
            }

        },
        TODOitem: {
            initial: "prompt",
            on: { ENDSPEECH: "init" },
            states: {
                prompt: { entry: say("Welcome to the TODOitem app")
            }
           }
        },
	Timer: {
	initial: "prompt",
	on: { ENDSPEECH: "init" },
	states: {
	prompt: { entry: say ("Welcome to the Timer app")
	}
	}
	},


        Appointment: {
            initial: "prompt",
            on: { ENDSPEECH: "who" },
            states: {
                prompt: { entry: say("Let's create an appointment") }
            }
        },
        who: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "person" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { person: grammar[context.recResult].person } }),
                    target: "day"

                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: say("Who are you meeting with?"),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry I don't know them"),
                    on: { ENDSPEECH: "prompt" }
                }
            }
        },
        day: {
            initial: "prompt",
            on:{
                RECOGNISED: [{
                    cond: (context) => "day" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { day: grammar[context.recResult].day } }),
                    target: "duration"
                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `OK. ${context.person}. On which day is your meeting?`
                    })),
		    on: { ENDSPEECH: "ask" }
                },
		ask: {
		     entry: listen()
            },
	    nomatch: {
	    	entry: say("Sorry, I don't understand"),
		on: { ENDSPEECH: "prompt" }
	    }
	  }
        },
	duration: {
            initial: "prompt",
            on: {
                RECOGNISED: [{cond: (context) => (boolGrammar[context.recResult] === b),
                    target: "time"
                },
		{cond: (context) => (boolGrammar[context.recResult] === a),
		target: "confirm_meeting_whole_day"
		},
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `OK. ${context.person} on ${context.day}. Will it take the whole day?`
                    })),
		    on: { ENDSPEECH: "ask" }
                },
		ask: {
		     entry: listen()
            },
	    nomatch: {
	    	entry: say("Sorry, I don't understand"),
		on: { ENDSPEECH: "prompt" }
	            }
                }
	},
	time: {
            initial: "prompt",
            on: { RECOGNISED: [{
                    cond: (context) => "time" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { time: grammar[context.recResult].time } }),
                    target: "confirm_time"
                },
                { target: ".nomatch" }]
		},
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `OK. ${context.person} on ${context.day}. What time is your meeting?`
                    })),
		    on: { ENDSPEECH: "ask" }
                },
		ask: {
		     entry: listen()
            },
	    nomatch: {
	    	entry: say("Sorry, I don't understand"),
		on: { ENDSPEECH: "prompt" }
	            }
                }
        },
	confirm_meeting_whole_day: {
            initial: "prompt",
            on: {
                RECOGNISED: [{cond: (context) => (boolGrammar[context.recResult] === b),
                    target: "init"
                },
		{cond: (context) => (boolGrammar[context.recResult] === a),
		target: "confirmed"
		},
                { target: ".nomatch" }]
            },

            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Do you want to create an appointment with ${context.person} on ${context.day} for the whole day?`
                    })),
		    on: { ENDSPEECH: "ask" }
                },
		ask: {
		     entry: listen()
            },
	    nomatch: {
	    	entry: say("Sorry, I don't understand"),
		on: { ENDSPEECH: "prompt" }
	           }
                }

	},
 	confirm_time: {
            initial: "prompt",
            on:  {
                RECOGNISED: [{cond: (context) => (boolGrammar[context.recResult] === b),
                    target: "who"
                },
		{cond: (context) => (boolGrammar[context.recResult] === a),
		target: "confirmed"
		},
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                   entry: send((context) => ({
                        type: "SPEAK",
                        value: `Do you want to create an appointment with ${context.person} on ${context.day} at ${context.time}?`
                    })),
		    on: { ENDSPEECH: "ask" }
                },
		ask: {
		     entry: listen()
            },
	    nomatch: {
	    	entry: say("Sorry, I don't understand"),
		on: { ENDSPEECH: "prompt" }
	           }
                },
            },
	    confirmed: {
            initial: "prompt",
            on: { ENDSPEECH: "init" },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Your appointment has been created!`
                    }))
                },
		}
		}
        }})
